import { Component, OnInit } from "@angular/core";
import { FormControl, FormGroup, NgForm, Validators } from "@angular/forms";
import { ActivatedRoute } from "@angular/router";
import { Store } from "@ngrx/store";
import { Observable } from "rxjs";
import { AppState } from "src/app/app.state";
import { Post } from "src/app/models/post.model";
import {
  deleteStart,
  loadPost,
  postStart,
  updateStart,
} from "../state/post.action";
import { getPosts, getPostById } from "../state/post.selector";

@Component({
  selector: "app-post",
  templateUrl: "./post.component.html",
  styleUrls: ["./post.component.scss"],
})
export class PostComponent implements OnInit {
  posts!: Observable<Post[]> | any;
  loginForm!: FormGroup;
  id: any;
  updatebutton = false;
  postbutton = false;
  constructor(private store: Store<AppState>, private route: ActivatedRoute) {
    this.loginForm = new FormGroup({
      email: new FormControl("", [Validators.required, Validators.email]),
      password: new FormControl("", [
        Validators.required,
        Validators.pattern("^[0-9]{5}"),
      ]),
    });
  }

  ngOnInit(): void {
    this.store.select(getPostById).subscribe((post) => {
      console.log(post);
      this.loginForm.patchValue({
        email : post?.email,
        password : post?.password
      })
    });
    this.store.dispatch(loadPost());
    this.posts = this.store.select(getPosts);
  }

  submit() {
    if (this.id) {
      this.store.dispatch(
        updateStart(this.loginForm.value)
      );
      this.posts = this.store.select(getPosts);
    } else {
      this.store.dispatch(postStart(this.loginForm.value));
      this.posts = this.store.select(getPosts);
    }
  }

  onDelete(id: any) {
    this.store.dispatch(deleteStart({ userId: id }));
    this.posts = this.store.select(getPosts);
  }

  submitForm(){
    console.log(this.loginForm.value);

    this.store.dispatch(postStart(this.loginForm.value));
    this.posts = this.store.select(getPosts);
  }
  postUser() {
    this.postbutton = true;
  }
}
