import { Component, OnInit } from "@angular/core";
import { FormControl, FormGroup, Validators } from "@angular/forms";
import { ActivatedRoute } from "@angular/router";
import { Store } from "@ngrx/store";
import { Observable } from "rxjs";
import { AppState } from "src/app/app.state";
import { postStart } from "src/app/effects/state/effect.action";
import { Post } from "src/app/models/post.model";
import { PostServiceService } from "../service/post-service.service";
import { deleteStart, loadPost, updateStart } from "../state/post.action";
import { getPosts } from "../state/post.selector";

@Component({
  selector: "app-post",
  templateUrl: "./post.component.html",
  styleUrls: ["./post.component.scss"],
})
export class PostComponent implements OnInit {
  posts!: Observable<Post[]>;
  data: any;
  loginForm!: FormGroup;
  oneUserD!: Observable<any>
  id: string | null;
  constructor(
    private store: Store<AppState>,
    private route: ActivatedRoute
  ) {
    this.loginForm = new FormGroup({
      email: new FormControl("", [Validators.required, Validators.email]),
      password: new FormControl("", [
        Validators.required,
        Validators.pattern("^[0-9]{5}"),
      ]),
    });
  }

  ngOnInit(): void {
    this.store.dispatch(loadPost());
    this.posts = this.store.select(getPosts);
  }

  submit(){
    this.id = this.route.snapshot.paramMap.get("id");
    if (this.id) {
      console.log(this.loginForm.value);

    } else {
      console.log(this.loginForm.value);
      this.store.dispatch(postStart(this.loginForm.value));
    }
  }
  oneUserData(id : any) {
    console.log(id);
  }

  onDelete(id : any){
    this.store.dispatch(deleteStart({userId : id}))
  }

  updateData(post : any){
    console.log(post);
    this.store.dispatch(updateStart(post));
  }
}
